                        </div>
                        <div className="sel_check">
                            {
                                ['=', '#', '$', '*', '|', '-'].indexOf(item['operator']) > -1 && (
                                    <Input onChange={(e) => handleInput(e, index, item)} style={{ marginLeft: '24px', marginBottom: '8px', width: 302 }} defaultValue={item['operand']} />
