 * @LastEditors: Please set LastEditors
 * @Description: 打开koroFileHeader查看配置 进行设置: https://github.com/OBKoro1/koro1FileHeader/wiki/%E9%85%8D%E7%BD%AE
 * @FilePath: \bl-device-manage-test\src\layouts\MessageManage\ListPage\index.js
 */
import { Avatar, List, message } from 'antd'
import React, { Component } from 'react'
import VirtualList from 'rc-virtual-list';
import { inject, observer } from 'mobx-react';


@inject('MessageStore')
@observer
class index extends Component {
    render() {
        const ContainerHeight = 800;
        const fakeDataUrl =
            'https://randomuser.me/api/?results=20&inc=name,gender,email,nat,picture&noinfo';
        const onScroll = e => {
            if (e.target.scrollHeight - e.target.scrollTop === ContainerHeight) {
                this.appendData();
            }
        };
        return (
            <List>
                <VirtualList
                    data={this.props.MessageStore.data}
                    height={ContainerHeight}
                    itemHeight={47}
                    itemKey="email"
                    onScroll={onScroll}
                >
                    {item => (
                        <List.Item key={item.email} extra={<div>21321</div>}>
                            {/* <List.Item.Meta
                                avatar={<Avatar src={item.picture.large} />}
                                title={<a href="https://ant.design">{item.name.last}</a>}
                                description={item.email}
                            /> */}
                            <div>Content</div>
                        </List.Item>
                    )}
                </VirtualList>
            </List>
        )
    }
    appendData = () => {
        fetch('https://randomuser.me/api/?results=20&inc=name,gender,email,nat,picture&noinfo')
            .then(res => res.json())
            .then(body => {
                this.props.MessageStore.setData(this.props.MessageStore.data.concat(body.results));
                message.success(`${body.results.length} more items loaded!`);
            });
    };
    componentDidMount() {
        this.appendData()
    }
}

export default index
