const DnDFlow = observer(({store}) => {
    const reactFlowWrapper = useRef(null);
    const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
    const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
    const [perArr, setPerArr] = useState([]);
    const [reactFlowInstance, setReactFlowInstance] = useState(null);
    const [nodeName, setNodeName] = useState('');
    const [nodeId, setNodeId] = useState('');
  
    const children = [];
    const field = [];
    let personList = [];
    let fieldProp = {};
    personList = Object.assign({},props.location.state.PersonListT)
    console.log(personList);
    Object.keys(personList).map((item) => {
      if (personList[item] != undefined) {
        children.push(<Option key={personList[item].id}>{personList[item].nickName}</Option>)
      }
    })
    // for (let i = 10; i < 36; i++) {
    //   children.push(<Option key={i.toString(36) + i}>{i.toString(36) + i}</Option>);
    // }
    let obj = Object.assign({}, props.location.state.itemData)
    Object.keys(obj).map((item) => {
      if (obj[item].properties != undefined && obj[item].properties.length != 0) {
        obj[item].properties.map((item) => {
          field.push({ propertyId: item.propertyId, name: item.name })
        })
      }
    })
    field.map((item) => {
      fieldProp[item.propertyId] = []
    })
    useEffect(() => { 
      setNodes((nds) =>
        nds.map((node) => {
          if (node.id === nodeId) {
            // it's important that you create a new object here
            // in order to notify react flow about the change
            node.data = {
              ...node.data,
              label: nodeName,
              person: perArr
            };
          }
          return node;
        })
      );
    }, [nodeName, setNodeName, perArr, setPerArr]);
  
    const onConnect = useCallback((params) => {
      let obj = {
        type: MarkerType.Arrow,
      }
      params.markerEnd = obj
      setEdges((eds) => addEdge(params, eds))
    }, []);
  
    const onDragOver = useCallback((event) => {
      event.preventDefault();
      event.dataTransfer.dropEffect = 'move';
    }, []);
  
    const onDrop = useCallback(
      (event) => {
        event.preventDefault();
  
        const reactFlowBounds = reactFlowWrapper.current.getBoundingClientRect();
        const type = event.dataTransfer.getData('application/reactflow');
  
        // check if the dropped element is valid
        if (typeof type === 'undefined' || !type) {
          return;
        }
  
        const position = reactFlowInstance.project({
          x: event.clientX - reactFlowBounds.left,
          y: event.clientY - reactFlowBounds.top,
        });
        const newNode = {
          id: getId(),
          type,
          position,
          data: { label: `${type} node` },
        };
  
        setNodes((nds) => nds.concat(newNode));
      },
      [reactFlowInstance]
    );
  
    const onSave = useCallback(() => {
      if (reactFlowInstance) {
        const flow = reactFlowInstance.toObject();
        personList = Object.assign({},props.location.state.PersonListT)
        let params = {}
        params.firstFormId = 1;
        params.edges = flow.edges
        params.nodes = []
        flow.nodes.map((item) => {
          let obj = {}
          obj.id = item.id
          obj.type = item.type
          obj.position = item.position
          obj.positionAbsolute = item.positionAbsolute
          console.log(item.data.person);
          if (item.data.person != undefined && item.data.person.length != 0) {
            console.log(Number(item.data.person));
            console.log(personList);
            let person = personList[Number(item.data.person) - 1]
            console.log(person);
            item.data.principal = person['username'];
            item.data.userId = person['id'];
          }
          obj.data = item.data
          params.nodes.push(obj)
        })
        console.log(params);
        // try {
        //   let res = services.putRequest(services.requestList.addFlow, params);
        //   if (isDataExist(res)) {
        //     return res;
        //   }
        // } catch (error) {
        //   console.log(error);
        // }
      }
    }, [reactFlowInstance]);
  
    const onNodeClick = useCallback((event, node) => {
      setNodeId(node.id)
      setPerArr(node.data.person)
      setNodeName(node.data.label)
    }, [nodeName], [nodeId])
  
    const handleChange = (value) => {
      setPerArr(value);
      console.log(`selected ${value}`);
    }
  
    function checkChange(checkedValues,a) {
      fieldProp[a] = checkedValues
    }
    return (
      <Layout>
        <Header className='header'>
          <Button type="primary" className='save' onClick={onSave}>保存</Button>
        </Header>
        <Layout>
          <Content>
            <Layout>
              <Header className='title'>
                <Sidebar />
              </Header>
              <Content>
                <div className="dndflow">
                  <ReactFlowProvider>
                    <Content>
                      <div className="reactflow-wrapper" ref={reactFlowWrapper}>
                        <ReactFlow
                          nodes={nodes}
                          edges={edges}
                          onNodesChange={onNodesChange}
                          onEdgesChange={onEdgesChange}
                          onConnect={onConnect}
                          onInit={setReactFlowInstance}
                          onDrop={onDrop}
                          onDragOver={onDragOver}
                          nodeTypes={nodeTypes}
                          onNodeClick={onNodeClick}
                          fitView
                        >
                          <Controls />
                          <MiniMap />
                        </ReactFlow>
                      </div>
                    </Content>
                  </ReactFlowProvider>
                </div>
              </Content>
            </Layout>
          </Content>
          <Sider style={{ backgroundColor: 'white' }} width='300'>
            <div className="updatenode__controls">
              <div>
                <label>节点名:</label>
                <input value={nodeName} onChange={(evt) => setNodeName(evt.target.value)} />
              </div>
              <div>
                <label>负责人：</label>
                <Select
                  // mode="multiple"
                  allowClear
                  style={{ width: '80%', marginTop: '10px' }}
                  placeholder="Please select"
                  value={perArr}
                  onChange={handleChange}
                >
                  {children}
                </Select>
              </div>
              {
                field.map((item) => {
                  return <div className='dataInfo'>
                    <label>{item.name}:</label>
                    <Checkbox.Group name={item.propertyId} options={[{ label: '可见', value: 'vis' }, { label: '可编辑', value: 'edit' }]} onChange={(checkedValues) => checkChange(checkedValues,item.propertyId)}></Checkbox.Group>
                  </div>
                })
              }
            </div>
          </Sider>
        </Layout>
  
      </Layout>
  
    );
})
export default inject(store => store)(DnDFlow);
