/*
 * @Author: EmberCCC 1810888456@qq.com
 * @Date: 2022-06-30 09:07:55
 * @LastEditors: EmberCCC 1810888456@qq.com
 * @LastEditTime: 2022-07-01 12:06:08
 * @FilePath: \bl-device-manage-test\src\components\GlobalTabel2\dataModal.js
 * @Description: 这是默认设置,请设置`customMade`, 打开koroFileHeader查看配置 进行设置: https://github.com/OBKoro1/koro1FileHeader/wiki/%E9%85%8D%E7%BD%AE
 */
import { Button, Checkbox, Popover } from 'antd'
import { Radio } from 'components/BLComps'
import { toJS } from 'mobx'
import { inject, observer } from 'mobx-react'
import React, { Component } from 'react'
import './index.css'

@inject('TableStore', 'HomeStore')
@observer
class DataModal extends Component {
    componentWillMount() {
        console.log(toJS(this.props.TableStore.columnsList));
        console.log(toJS(this.props.TableStore.modalEditData));
        // console.log(toJS(this.props.TableStore.));
    }
    render() {
        const { columnsList, itemIndex, modalEditData, dataSource } = this.props.TableStore
        const getData = () => {
            return (
                columnsList.map((item) => {
                    return (
                        <div className='item_content'>
                            <div className='item_title'>
                                {item.name}
                            </div>
                            <div className='item_article'>
                                {modalEditData[item.id]}
                            </div>
                        </div>
                    )
                })
            )
        }
        const handleClickFront = () => {
            this.props.TableStore.setValue('itemIndex', itemIndex - 1);
            console.log(dataSource.filter((item) => {
                return item.key == itemIndex - 1;
            })[0]);
            this.props.TableStore.setValue('modalEditData', dataSource.filter((item) => {
                return item.key == itemIndex - 1;
            })[0])
        }
        const handleClickBehind = () => {
            console.log(dataSource.filter((item) => {
                return item.key == itemIndex + 1;
            }));
            this.props.TableStore.setValue('itemIndex', itemIndex + 1);
            this.props.TableStore.setValue('modalEditData', dataSource.filter((item) => {
                return item.key == itemIndex + 1;
            })[0])
        }
        const judgeFront = () => {
            if (itemIndex == 1) {
                return true;
            } else {
                return false;
            }
        }
        const judgeBehind = () => {
            if (itemIndex == dataSource.length) {
                return true;
            } else {
                return false;
            }
        }
        const fieldChoose = (
            <>
                {/* <Checkbox indeterminate={indeterminate} onChange={onCheckAllChange} checked={checkAll}>
                    Check all
                </Checkbox> */}
                <Checkbox.Group onChange={checkChange} defaultValue={this.props.TableStore.fieldValue}>
                    {columns.map((item, index) => {
                        return <>
                            <Checkbox value={item.key} key={index} style={{width:'300px'}}>{item.title}</Checkbox>
                            <br />
                        </>
                    })}
                </Checkbox.Group>
            </>
        )
        const checkChange = (value) => {
            this.props.TableStore.setFieldValue(value);
            console.log(toJS(this.props.TableStore.fieldValue));
        }
        const visibleChange = (visible) => {
            if (visible == false) {
                this.props.TableStore.setLastColumns();
            }
        }
        return (
            <div className='main_modal'>
                <div className='left_main'>
                    <div className='left_modal'>
                        <div className='left_header_left'>
                            <div style={{ border: 'none', margin: '0 10px 10px 10px', verticalAlign: 'middle', float: 'right' }}>
                                <Popover placement="bottomRight" content={fieldChoose} trigger="click"
                                    onVisibleChange={visibleChange}
                                >
                                    <Button icon={<FunnelPlotOutlined />} style={{ border: 'none', margin: '0 10px 10px 0', verticalAlign: 'middle', float: 'right' }} />
                                </Popover>
                            </div>
                            <Button>分</Button>
                            <Button>打</Button>
                            <Button>复</Button>
                            <Button>编</Button>
                            <Button>删</Button>
                        </div>
                        <div className='left_header_right'>
                            <Button size='small' disabled={judgeFront()} type='text' onClick={handleClickFront}>←</Button>
                            <span type='text' >{itemIndex}/{dataSource.length}</span>
                            <Button size='small' disabled={judgeBehind()} type='text' onClick={handleClickBehind}>→</Button>
                        </div>
                    </div>
                    <div className='left_content'>
                        {getData()}
                    </div>
                </div>
                <div className='right_modal'>
                    <div className='right_header'>
                        <Radio.Group onChange={(value) => console.log(value)} defaultValue="1" style={{ paddingLeft: '5px' }}>
                            <Radio.Button value={'1'}>数据日志</Radio.Button>
                            <Radio.Button value={'2'}>评论</Radio.Button>
                        </Radio.Group>
                    </div>
                </div>
            </div>
        )
    }
}

export default DataModal
