import React from 'react'
import { Input, Divider, Radio, Select, Button,Badge } from 'antd'
import { circleStatus } from 'components/StateLess'
const { Option } = Select;

/* 设备定义-step == 0 1 modal  */
export function setDeviceTypeForms({
  current, status, currentMain, selectList
}) {
  let columns = [];
  if (current === '2') {
    columns = [
      {
        key: 'modelName',
        label: '型号名称',
        value: <Input placeholder='请输入' style={{ width: '330px' }} />,
        getFieldDecorator: {
          initialValue: status === 'edit' ? currentMain.modelName : undefined,
          rules: [
            {
              required: true,
              message: '请输入型号名称'
            }
          ]

        }
      },
      {
        key: 'typeId',
        label: '设备类型',
        value: (
          <Select
            optionFilterProp='children'
            showSearch allowClear
            style={{ width: 330, }}
            placeholder='选择设备类型'
          >
            {
              selectList && selectList.map(item => {
                return <Option key={item.id} value={item.id}>
                  {item.id}_{item.typeName}
                </Option>
              })
            }
          </Select>
        ),
        getFieldDecorator: {
          initialValue: status === 'edit' ? currentMain.typeId : undefined,
          rules: [
            {
              required: true,
              message: '必填'
            }
          ]
        }
      },
      {
        key: 'modelStatus',
        label: '型号状态',
        value: (
          <Radio.Group>
            <Radio value={1}>启用</Radio>
            <Radio value={0}>禁用</Radio>
          </Radio.Group>
        ),
        getFieldDecorator: {
          initialValue: status === 'edit' ? currentMain.modelStatus : 1,
        }
      },

    ]
  } else {
    columns = [
      {
        key: 'typeName',
        label: '设备类型名称',
        value: <Input placeholder='请输入' style={{ width: '330px' }} />,
        getFieldDecorator: {
          initialValue: status === 'edit' ? currentMain.typeName : undefined,
          rules: [
            {
              required: true,
              message: '请输入设备类型名称'
            }
          ]

        }
      },
      {
        key: 'typeStatus',
        label: '设备状态',
        value: (
          <Radio.Group>
            <Radio value={1}>启用</Radio>
            <Radio value={0}>禁用</Radio>
          </Radio.Group>
        ),
        getFieldDecorator: {
          initialValue: status === 'edit' ? currentMain.typeStatus : undefined,
        }
      },
      {
        key: 'unit',
        label: '计量单位',
        value: (
          <Input style={{ width: 330, }}
            placeholder='输入计量单位' />
        ),
        getFieldDecorator: {
          initialValue: status === 'edit' ? currentMain.unit : undefined,
        }
      },
    ]
  }
  return columns;
}
/* 设备定义-step == 0 1 table  */
export function setDeviceTypeColumns(
  { current, handleEdit, handleStatus, handleFile }
) {
  let columns = []
  if (current === '1') {
    columns = [
      {
        title: '设备类型名称',
        dataIndex: 'typeName',
        key: 'typeName',
      }, {
        title: '设备类型状态',
        dataIndex: 'typeStatus',
        key: 'typeStatus',
        render: value => (
          <div>
         {value?<Badge status="success" text="启用" />:
          <Badge status="error" text="禁用" />}
          </div>
        )
      }
    ]
  } else {
    columns = [
      {
        title: '型号名称',
        dataIndex: 'modelName',
        key: 'modelName',
      }, {
        title: '设备类型',
        dataIndex: 'typeName',
        key: 'typeName',
        render: value => value || '-'
      }, {
        title: '设备型号状态',
        dataIndex: 'modelStatus',
        key: 'modelStatus',
        render: value => circleStatus(value, 'device')
      }

    ]
  }
  return columns.concat([
    {
      title: '操作',
      dataIndex: 'id',
      key: 'operate', align: 'right',
      render: (value, record) => {
        let status = current === '1' ? record.typeStatus : record.modelStatus;
        return <span style={{ color: '#6236FF', cursor: 'pointer' }}>
          {current === '2' && <React.Fragment>
            <span onClick={() => handleFile(record)}>查看知识库</span>
            <Divider type='vertical' />
          </React.Fragment>}
          <span onClick={() => handleEdit(record)}>编辑</span>
          <Divider type='vertical' />
          <span onClick={() => handleStatus(record)}>{
            status !== 1 ? '启用' : '禁用'
          }</span>
        </span>
      }
    }
  ]);
}

export function setDeviceFileColumns({
  handleFileExport, handleFileDel
}) {
  return [
    {
      title: '文档类型',
      dataIndex: 'innertype',
      key: 'innertype',
      render: value => {
        let current = setFileType().filter(item => item.id === value)[0]
        return current.typeName
      }
    }, {
      title: '文件名称',
      dataIndex: 'filename',
      key: 'filename',
    },
    {
      title: '操作',
      dataIndex: 'id',
      key: 'id',
      align: 'right',
      render: (value, record) => <span style={{ color: '#6236FF', cursor: 'pointer' }}>
        <span onClick={() => handleFileExport(record)}>下载</span>
        <Divider type='vertical' />
        <span onClick={() => handleFileDel({ type: 'single', value })}>删除</span>
      </span>
    }
  ]
}

export function setFileType() {
  return [
    {
      id: 1, typeName: '运维文档'
    },
    {
      id: 2, typeName: '操作手册'
    },
    {
      id: 3, typeName: '员工交流'
    },
  ]
}

/* 设备列表信息 */
export function setDevListColumns({
  onEdit, onDelete, onCodeQuery
}) {
  return [
    { title: '设备名称', dataIndex: 'deviceName', key: 'deviceName', render: (value) => value || '-' },
    {
      title: '设备编号', dataIndex: 'deviceCode', key: 'deviceCode',
      render: (value) => value || '-'
    },
    {
      title: '型号', dataIndex: 'modelName', key: 'modelName',
      render: (value) => value || '-'
    },
    {
      title: '保修期（天）', dataIndex: 'guaranteePeriod', key: 'guaranteePeriod',
      render: (value) => <span>{value || '-'}</span>
    },
    {
      title: '计量单位', dataIndex: 'unit', key: 'unit',
      render: (value) => value || '-'
    },
    {
      title: '创建时间', dataIndex: 'deviceTime', key: 'deviceTime',
