import React from 'react'
import { Select,Input } from 'antd'
const { Option } = Select

class TimeInput extends React.Component {
  static getDerivedStateFromProps(nextProps) {
    if ('value' in nextProps) {
      return {
        ...(nextProps.value || {}),
      };
    }
    return null;
  }

  constructor(props) {
    super(props);

    const value = props.value || {};
    this.state = {
      waitTime: value.waitTime || 0,
      excuteUnit: value.excuteUnit || '小时',
    };
  }

  handleTimeChange = e => {
    const waitTime = e.target.value;
    if (!('value' in this.props)) {
      this.setState({ waitTime });
    }
    this.triggerChange({ waitTime });
  };

  handleUnitChange = excuteUnit => {
    if (!('value' in this.props)) {
      this.setState({ excuteUnit });
    }
    this.triggerChange({ excuteUnit });
  };

  triggerChange = changedValue => {
    // Should provide an event to pass value to Form.
    const onChange = this.props.onChange;
    if (onChange) {
      onChange(Object.assign({}, this.state, changedValue));
    }
  };

  render() {
    const { size } = this.props;
    const state = this.state;
    return (
      <span>
        <Input
          type="text"
          size={size}
          value={state.waitTime}
          onChange={this.handleTimeChange}
          style={{ width: '50%', marginRight: '3%' }}
        />
        <Select
          value={state.excuteUnit}
          size={size}
          style={{ width: '30%' }}
          onChange={this.handleUnitChange}
        >
          <Option value="小时">小时</Option>
          <Option value="分">分</Option>
        </Select>
      </span>
    );
  }
}

export default TimeInput;
