/*
 * @Author: your name
 * @Date: 2022-04-02 11:12:08
 * @LastEditTime: 2022-04-10 11:19:00
 * @LastEditors: Please set LastEditors
 * @Description: 打开koroFileHeader查看配置 进行设置: https://github.com/OBKoro1/koro1FileHeader/wiki/%E9%85%8D%E7%BD%AE
 * @FilePath: \bl-device-manage-test\src\layouts\BasicManage\ComputerPage\index.js
 */

import { inject, observer } from 'mobx-react'
import React, { Component } from 'react'
import { Table, Modal, message, Divider } from 'antd'
import GlobalForm from 'components/GlobalForm';
import { toJS } from 'mobx';
import GlobalTabel from 'components/GlobalTabel';


@inject('BasicStore')
@inject('HomeStore')
@observer
class ComputerPage extends React.Component {

  render() {
    const { model } = this.props.HomeStore;
    const { dataSource } = this.props.HomeStore;
    const { PageInfo } = this.props.HomeStore;
    const {firstFormId} = this.props.HomeStore
    const {secondFormId} = this.props.HomeStore
    const {isLoading} = this.props.HomeStore
    let columns = toJS(this.props.HomeStore.columns)
    if (model == 'look') {
      return <Table columns={columns} dataSource={dataSource} pagination={PageInfo} onChange={this.onChange} loading={isLoading}></Table>;
      // return <Table columns={this.getColumns()} dataSource={dataSource} pagination={PageInfo}></Table>;
    } else if(model == 'submit'){
      return <GlobalForm loading={isLoading}/>
    }else {
      return <GlobalTabel columns={columns} dataSource={dataSource} PageInfo={PageInfo} onChange={this.onChange} firstFormId={firstFormId} loading={isLoading}
      secondFormId={secondFormId} del={(params)=>this.props.HomeStore.deleteObj(params)}/>
    }
  }
  onChange = (e) => {
    const { model } = this.props.HomeStore;
    this.props.HomeStore.PageInfo = e;
    this.props.HomeStore.PageInfo.pageIndex = e.current
    console.log(model);
    this.getData()
  }
  getColumns = () => {
    const {columns} = this.props.HomeStore
    toJS(columns)
    return columns
  }
  
  edit = (txt) => {
    console.log(txt);
  }


  getData = () => {
    const { PageInfo, firstFormId } = this.props.HomeStore;
    let params = {};
    params.firstFormId = firstFormId
    params.pageIndex = PageInfo.pageIndex
    params.pageSize = PageInfo.pageSize
    this.props.HomeStore.queryAll(params);
  }
  getField = () => {
    const {firstFormId} = this.props.HomeStore
    let params = {};
    params.firstFormId = firstFormId;
    this.props.HomeStore.queryField(params)
  }
  componentWillMount() {
    const {firstFormId} = this.props.HomeStore
    let params = {};
    params.firstFormId = firstFormId;
    this.getField();
    this.getData();
    this.getColumns();
  }
}

export default ComputerPage;
