import React from 'react'
import { inject, observer } from 'mobx-react'
import { toJS } from 'mobx'
import { Modal, message } from 'antd'
import SearchBar from './SearchBar'
import Table from './Table'
import AddModal from './AddModal'
import QrCanvas from './QrCanvas'
import '../index.less'

@inject('DeviceDefineStore')
@observer
class DefinePage extends React.Component {
  constructor(props) {
    super(props)
    this.store = this.props.DeviceDefineStore;
  }
  render() {
    const { listVisible, listStatus } = toJS(this.store);
    return <div className='define-container'>
      <SearchBar />
      <Table
        onDelete={this.onDelete}
      />
      {
        listVisible && <AddModal
          status={listStatus}
          onClose={() => this.store.listVisible = false}
        />
      }
      {this.props.DeviceDefineStore.codeVisible && <QrCanvas />}
    </div>;
  }
  onDelete = (value) => {
    Modal.confirm({
      title: '提示',
      content: '确定删除当前信息？',
      okText: '确定',
      cancelText: '取消',
      onOk: () => {
        this.store.deleteDevice({
          id: value
        })
      }
    });
  }
  onStepChange = (step) => {
    this.store.current = step;
    this.store.changeStep();
  }
}

export default DefinePage
