import { get, put } from '../utils/request';

const requestList = {
  /* 设备定义 */
  /* ----设备类型 */
  getType: '/manage/base/deviceType/list',
  saveType: '/manage/base/deviceType/save',
  deleteType: '/manage/base/deviceType/delete',
  batchDeleteType: '/manage/base/deviceType/batchDelete',

  /* ----设备型号 */
  getModel: '/manage/base/deviceModel/list',
  saveModel: '/manage/base/deviceModel/save',
  deleteModel: '/manage/base/deviceModel/delete',
  batchDeleteModal: '/manage/base/deviceModel/batchDelete',

  getSelectType: '/manage/base/deviceType/listAll',
  getSelectModel: '/manage/base/deviceModel/listByTypeId',
  getSelectDevice: '/manage/base/device/listByModelId',

  getDeviceFileList: '/manage/base/file/getFileList',
  deleteFile: '/manage/base/file/deleteFile',
  downloadFile: '/manage/base/file/download',

  /* ----设备列表 */
  getDevList: '/manage/base/device/list',//获取设备列表
  getDevInfo: '/manage/base/device/getInfo',
  getUsersByRoles: '/manage/sys/user/getWorkUsers',
  deleteDevice: '/manage/base/device/delete',
  saveDevice: '/manage/base/device/save',
  saveQrcode: '/manage/base/qrcode/save',
  getQrcode: '/manage/base/qrcode/list',
  downloadModule: '/manage/base/device/download',
  ImportModule: '/manage/base/device/import',

  /* 设备台账 */
  getDevStanding: '/manage/base/device/list1',

  /* 设备配置 */
  getCustomerList: '/manage/sys/customer/list',

  /* 设备日志 */
  getDeviceLog: '/manage/base/deviceLog/list',
  getDetailLog: '/manage/result/getByLogId',

  /* 维护配置 */
  getMaintainServer: '/manage/base/config/list',
  getServerInfo: '/manage/base/config/getInfo',
  saveServer: '/manage/base/config/save',
  deleteServer: '/manage/base/config/delete',

  getMaintainSchema: '/manage/base/configPath/list',
  getSchemaInfo: '/manage/base/configPath/getInfo',
  saveSchema: '/manage/base/configPath/save',
  deleteSchema: '/manage/base/configPath/delete',


  /* title 配置页面 */
  getTitle:'/manage/base/title/list',
  saveTitle:'/manage/base/title/save',


}

export function gets(type) {
  return async function (data, options) {
    return await get(requestList[type], data, options);
  }
}
export function posts(type) {
  return async function (data, options) {
    return await put(requestList[type], data, options);
  }
}
