/*
 * @Author: your name
 * @Date: 2022-04-24 12:36:43
 * @LastEditTime: 2022-05-05 21:30:23
 * @LastEditors: EmberCCC 1810888456@qq.com
 * @Description: 打开koroFileHeader查看配置 进行设置: https://github.com/OBKoro1/koro1FileHeader/wiki/%E9%85%8D%E7%BD%AE
 * @FilePath: \bl-device-manage-test\src\layouts\MessageManage\ListPage\index.js
 */
import { List, message } from 'antd'
import React, { Component } from 'react'
import VirtualList from 'rc-virtual-list';
import { inject, observer } from 'mobx-react';
import DetailPage from '../DetailPage'
import { toJS } from 'mobx';
import { withRouter } from 'react-router-dom';


@inject('MessageStore')
@observer
@withRouter
class index extends Component {
    render() {
        const ContainerHeight = 800;
        const { modalVisible } = this.props.MessageStore
        // const { id } = this.props.location.state || ''
        const onScroll = e => {
            if (e.target.scrollHeight - e.target.scrollTop === ContainerHeight) {
                this.appendData();
            }
        };
        const changeVisible = () => {
            this.props.MessageStore.changeModal();
            // console.log(modalVisible);
            // console.log(11);
        }

        return (
            <List>
                <VirtualList
                    data={this.props.MessageStore.data}
                    height={ContainerHeight}
                    itemHeight={47}
                    onScroll={onScroll}
                >
                    {item => (
                        <List.Item key={321} extra={<div>21321</div>} onClick={() => changeVisible()} style={{ border: '1px solid grey', margin: '5px', padding: '4px' }}>
                            <div>
                                <div>
                                    维修工单：1
                                </div>
                                <div>
                                    维修工单：1
                                </div>
                                <div>
                                    维修工单：1
                                </div>
                            </div>
                            <div>
                                <div>
                                    11
                                </div>
                            </div>
                            <div>
                                <div>
                                    11
                                </div>
                            </div>
                        </List.Item>
                    )}
                </VirtualList>
                {modalVisible && <DetailPage />}
            </List>
        )
    }
    getList = (nowL) => {
        switch (nowL) {
            case 'todo':
                this.setState({
                    infoList: toJS(this.props.MessageStore.todoList)
                })
                break;
            case 'createList':
                this.setState({
                    infoList: toJS(this.props.MessageStore.createList)
                })
                break;

            case 'handle':
                this.setState({
                    infoList: toJS(this.props.MessageStore.handleList)
                })
                break;

            case 'copyList':
                this.setState({
                    infoList: toJS(this.props.MessageStore.copyList)
                })
                break;

            default:
                break;
        }
        console.log(this.state.infoList);
    }
    componentDidMount() {
        const location = this.props.location.pathname
        const nowL = location.split('/')[2];
        console.log(nowL);
        this.getList(nowL);
        // console.log(this.props.MessageStore.handleList);
    }
}

export default index
