/*
 * @Author: EmberCCC 1810888456@qq.com
 * @Date: 2022-07-04 12:43:55
 * @LastEditors: EmberCCC 1810888456@qq.com
 * @LastEditTime: 2022-07-07 22:12:05
 * @FilePath: \bl-device-manage-test\src\layouts\FormEdit\self_item\link_item.js
 * @Description: 这是默认设置,请设置`customMade`, 打开koroFileHeader查看配置 进行设置: https://github.com/OBKoro1/koro1FileHeader/wiki/%E9%85%8D%E7%BD%AE
 */
import React, { useEffect, useState } from "react";
import { Button, Input, Modal, Tooltip } from "antd";
import Math_Modal from "../FormModal/Math_Modal";
import './index.css'
import { DeleteTwoTone } from "@ant-design/icons";
export const Submit_check = ({ value, onChange, ...rest }) => {
    const [math, setMath] = useState(false);
    const [relValue, setRelValue] = useState("");
    const [checkList, setCheckList] = useState([]);
    const { addons } = rest;
    useEffect(() => {
        console.log(checkList);
    },[checkList])
    const handleInput = (event) => {
        onChange(event.target.value);
    }
    const handleLink = () => {
        console.log(1);
    }
    const openMath = () => {
        setMath(true);
    }
    const closeMath = () => {
        setMath(false);
    }
    const handleAdd = (info) => {
        if (info == 'true') {
            let list = checkList;
            list.push(relValue);
            setCheckList(list)
            onChange(checkList)
        }
        closeMath();
    }
    const handleDel = (index) => {
        console.log(index);
        let list = checkList;
        let newList = list.splice(index, 1);
        console.log(newList);
        setCheckList([...newList])
    }
    const title_item = (
        <div>
            <span className="title_left">提交校验</span>
            <span className="title_right">使用数学运算符编辑公式</span>
        </div>
    )
    return (
        <div style={{ display: 'flex', flexDirection: 'column', width: '100%' }}>
            {
                checkList.map((item, index) => {
                    return (
                        <div key={index} className='check_item_one'>
                            <Tooltip title={item}>
                                <div className='check_item'>{item}</div>
                            </Tooltip>
                            <Tooltip title={'删除'}>
                                <DeleteTwoTone twoToneColor="#DC143C" onClick={() => handleDel(index)} />
                            </Tooltip>
                        </div>
                    )
                })
            }
            <Button onClick={openMath}>添加校验条件</Button>

            <Modal
                visible={math}
                onCancel={closeMath}
                title={title_item}
                footer={null}
                width={800}
                destroyOnClose={true}
            >
                <Math_Modal title={"公式"} setValue={setRelValue} addItem={handleAdd} />
            </Modal>
        </div>
    )
}
