import React, { Component } from 'react';
import { inject, observer } from 'mobx-react';
import GlobalList from 'components/GlobalList';
import AddModal from './AddModal'
import { setWorkColumns, } from '../methods';

@inject('WorkStore', 'DeviceDefineStore')
@observer
class DefineTable extends Component {
	constructor(props) {
		super(props);
		this.store = this.props.WorkStore;
		this.state = {
			visible: false,
			selectedRowKeys: [],
			fileVisible: false
		}
	}
	render() {
		const {
			isLoading, workList, workPage, workCols
		} = this.store;
		// const { customerList,selectType } = this.props.DeviceDefineStore;
		return (
			<div>
				<GlobalList
					table={{
						rowKey: 'id',
						columns: setWorkColumns({
							cols:workCols, handleDistribute: this.handleDistribute, handleDetail: this.handleDetail
						}),
						dataSource: workList,
						pagination: {
							...workPage,
							onChange: (pageIndex, pageSize) => {
								this.store.getWork({
									pageIndex,
									pageSize,
								});
							}
						},
						loading: isLoading,
					}}
				/>
				{
					this.state.visible && <AddModal
						visible={this.state.visible}
						onClose={this.onClose}
					/>
				}
			</div>
		);
	}
	handleDistribute = record => {
		this.store.currentMain = record;
		this.setState({
			visible: true
		});
	};
	handleDetail = record => {
		this.store.currentMain = record;
		this.store.isDaily = true;
	}
	onClose = () => {
		this.setState({
			visible: false
		})
	}
	/* modal - operate */
	handleVisible = () => {
		this.store.visible = !this.store.visible;
	};
	/* 弹窗提交操作 */
	handleSubmit = values => {
		const { currentMain } = this.store;
		let params = {
			...values,
			...currentMain
		}
		this.store.saveType(params);
		this.handleVisible();
	};
	componentWillUnmount() {
		// this.store.workList = []
		// this.store.workPage = {}
	}
}
export default DefineTable;
