import React, { Component } from 'react';
import { inject, observer } from 'mobx-react';
import { Radio } from 'antd';
import RepairServer from './Server'
import RepairSchema from './Schema'
import RepairOperate from './Operate'
import RepairQuery from './Query'
import RepairPlan from './Plan'
@inject('ZCloudDeviceStore')
@observer
class DeviceRepair extends Component {
	state = {
		radio: 1
	}
	render() {
		const { radio } = this.state;
		return <div className='tabs_container'>
			<Radio.Group style={{ marginBottom: 20 }} defaultValue='1' onChange={this.handleRadio}>
				<Radio.Button value='1'>点检库</Radio.Button>
				<Radio.Button value='2'>点检方案</Radio.Button>
				<Radio.Button value='5'>点检计划</Radio.Button>
				<Radio.Button value='3'>点检操作</Radio.Button>
				<Radio.Button value='4'>点检结果</Radio.Button>

			</Radio.Group>
			{radio === 1 && <RepairServer type='repair' />}
			{radio === 2 && <RepairSchema type='repair' />}
			{radio === 3 && <RepairOperate type='repair' />}
			{radio === 4 && <RepairQuery type='repair' />}
			{radio === 5 && <RepairPlan type='repair' />}

		</div>
	}
	handleRadio = e => {
		this.setState({
			radio: parseFloat(e.target.value)
		})
	};
	componentWillMount() {
		this.props.ZCloudDeviceStore.modifyValue('planType', 1)
		this.props.ZCloudDeviceStore.modifyValue('maintainType', 1)
	}

}
export default DeviceRepair;
