import React, { Component } from 'react';
import { inject, observer } from 'mobx-react';
import { Table } from 'antd';
import { toJS } from 'mobx';


@inject('SparepartsStore')
@observer
class StockTable extends Component {
    render() {
        const { tableData, PageInfo, isLoading, inPutLogData, columnsType } = this.props.SparepartsStore
        return <div style={{ marginTop: 20 }}>
            <Table rowKey="id" size='middle' loading={isLoading} columns={this.getColumns()} dataSource={columnsType === '1' ? tableData : inPutLogData} pagination={PageInfo} onChange={this.pageChange} />
        </div>;
    }
    pageChange = (e) => {
        this.props.SparepartsStore.PageInfo = e
        this.props.SparepartsStore.PageInfo.pageIndex = e.current
        const { getData, PageInfo, columnsType, spareName, getLogData, getAllSpare } = this.props.SparepartsStore
        columnsType === '1' ? getData(2, { spareName, pageIndex: PageInfo.pageIndex, pageSize: PageInfo.pageSize }) : getLogData({ pageIndex: PageInfo.pageIndex, pageSize: PageInfo.pageSize })
    }
    checkLog = (id) => {
        this.props.SparepartsStore.columnsType = '2'
        const { getLogData } = this.props.SparepartsStore
        getLogData({ spareId: id })

    }
    getColumns = () => {
        const { columnsType } = this.props.SparepartsStore
        if (columnsType === '1') {
            return [

                {
                    title: '备件名称',
                    dataIndex: 'spareName',
                    key: 'spareName',
                },
                {
                    title: '备件编号',
                    dataIndex: 'model',
                    key: 'model',
                },
                {
                    title: '类型',
                    dataIndex: 'spareTypeName',
                    key: 'spareTypeName',
                },
                {
                    title: '库存数量',
                    dataIndex: 'stock',
                    key: 'stock',
                },
                {
                    title: '预警下限',
                    dataIndex: 'warnMin',
                    key: 'warnMin',
                },
                {
                    title: '预警上限',
                    dataIndex: 'warnMax',
                    key: 'warnMax',
                },
                {
                    title: '操作',
                    key: 'x',
                    align: 'right',
                    render: (text, record) => (
                        <span>
                            <a onClick={e => this.checkLog(record.id)}>查看记录</a>
                        </span>
                    )
                }
            ];
        } else {
            return [

                {
                    title: '备件名称',
                    dataIndex: 'spareName',
                    key: 'spareName',
                },
                {
                    title: '类型',
                    dataIndex: 'typeName',
                    key: 'typeName',
                },
                {
                    title: '数量',
                    dataIndex: 'number',
                    key: 'number',
                },
                {
                    title: '备件编号',
                    dataIndex: 'model',
                    key: 'model',
                },
                {
                    title: '出入库标志',
                    dataIndex: 'type',
                    key: 'type',
                    render: (text) => (<div>{text === 1 ? '入库' : text === 2 ? '出库' : '还库'}</div>)
                },
                {
                    title: '操作人',
                    dataIndex: 'userName',
                    key: 'userName',
                },
                {
                    title: '操作时间',
                    dataIndex: 'createTime',
                    key: 'createTime',
                },
                {
                    title: '备注',
                    dataIndex: 'remark',
                    key: 'remark',
                },
                {
                    title: '操作',
                    dataIndex: 'x',
                    key: 'x',
                    render: (text, record) => (<div>{record.type !== 1 ? <a onClick={e => this.props.history.push(`/device/work?tab=2&workNo=${record.workNo}`)}>详情</a> : '-'}</div>)
                }
            ];
        }
    }
    componentDidMount() {
        const { getData, PageInfo, columnsType, spareName, getLogData, getAllSpare } = this.props.SparepartsStore
        columnsType === '1' ? getData(2, { spareName, pageIndex: PageInfo.pageIndex, pageSize: PageInfo.pageSize }) : getLogData({ pageIndex: PageInfo.pageIndex, pageSize: PageInfo.pageSize })
        getAllSpare()
    }
}
export default StockTable;
