/*
 * @Author: your name
 * @Date: 2022-04-05 11:02:45
 * @LastEditTime: 2022-04-05 16:29:39
 * @LastEditors: Please set LastEditors
 * @Description: 打开koroFileHeader查看配置 进行设置: https://github.com/OBKoro1/koro1FileHeader/wiki/%E9%85%8D%E7%BD%AE
 * @FilePath: \bl-device-manage-test\src\components\GlobalForm\index.js
 */

import React, { useState } from 'react'
import { Form, Button, Select } from 'antd'
import { GlobalComponent } from 'layouts/TableEdit/config';
import { inject ,observer} from 'mobx-react';
import { checkCurrentMenu } from 'utils/dataTools';
import { MenuObj } from 'constants/configs';
import { toJS } from 'mobx';

const { Option } = Select;
@inject('HomeStore')
@observer
class GlobalForm extends React.Component{
    state = {
        itemData:[]
    }
    render(){
        const {selectedKeys} = this.props.HomeStore;
        
        const handleLabelChange = (e) => {
            const x = Object.keys(e);
            this.state.itemData.map(item=>{
                if(item.label == x[0]){
                    item.attr.defaultValue = e[x[0]];}
                })
            // itemData[curItemKey].label = val;
        }
    
        const renderDiffComponents = (item, indexs, ComponentInfo) => {
            switch (item.name) {
                case 'Divider':
                    return <ComponentInfo key={indexs} {...item.attr}></ComponentInfo>
                case 'Select':
                    return (
                        <ComponentInfo key={indexs} defaultValue={item.attr.defaultValue}>
                            {
                                item.attr.options.map(subItem => <Option key={subItem.key} value={subItem.value + ''}>{subItem.label}</Option>)
                            }
                        </ComponentInfo>
                    )
                default:
                    return <ComponentInfo key={indexs} {...item.attr} />
            }
        }
        // 递归函数
        const loop = (arr, index) => {
            return (
                arr.map((item, i) => {
                    const indexs = index === '' ? String(i) : `${index}-${i}`;
                    if (item) {
                        if (item.children) {
                            return (
                                <div {...item.attr} data-id={indexs} key={indexs}>
                                    {loop(item.children, indexs)}
                                </div>
                            )
                        }
                        const ComponentInfo = GlobalComponent[item.name]
                        return (
                            <Form.Item
                                key={indexs}
                                label={item.label}
                                name={item.label}
                                className='formItemStyle'
                            >
                                {
                                    renderDiffComponents(item, indexs, ComponentInfo)
                                }
                            </Form.Item>
                        )
                    } else {
                        return null
                    }
                })
            )
        };
    
        const sub = (values) => {
            this.state.itemData =  this.props.HomeStore.changeItemData();
            let now = selectedKeys[0];
            console.log(now);
            console.log(this.props.HomeStore.itemData);
            let menuObj = toJS(MenuObj.leafMenuModels);
            let current = checkCurrentMenu({ menuObj, now });
            console.log(current);
            console.log(this.state.itemData);
        }
        return <div>
            <Form layout={'vertical'} onFinish={sub} onValuesChange={handleLabelChange}>
                {loop(this.state.itemData, '')}
                <Form.Item>
                    <Button type="primary" onClick={sub}>Submit</Button>
                </Form.Item>
            </Form>
        </div>
    }
    componentDidMount(){
        this.setState({itemData:this.props.HomeStore.changeItemData()})
    }
}
export default GlobalForm
