import React, { Component } from 'react';
import { inject, observer } from 'mobx-react';
import { Button, Input } from 'antd';

const { Search } = Input
@inject('DeviceDefineStore')
@observer
class SearchBar extends Component {
	render() {
		return <div>
			<Input
				allowClear
				placeholder={this.props.DeviceDefineStore.current === 1 ? "请输入设备类型名称" : '请输入设备型号名称'}
				style={{
					width: 250
				}}
			/>
			<Button	
				type="primary btn-border-purple " 
				onClick={this.onSearch}
				style={{
					marginLeft: '10px'
				}}>
				查询
			</Button>
			<Button
				type='primary'
				className="add-btn"
				style={{
					marginLeft: '10px'
				}}
				onClick={this.onModalShow.bind(this, true)}
			>
				<i className='iconfont icon-add icon-spacing' style={{fontSize:'12px'}}></i>
				新增
			</Button>

		</div>;
	}
	onSearch = value => {
		this.props.DeviceDefineStore.searchName = value;
		this.props.DeviceDefineStore.getType({})
	}
	onModalShow = (visible) => {
		this.props.DeviceDefineStore.status = 'add'
		this.props.DeviceDefineStore.visible = visible
	}

}
export default SearchBar;
