import React from 'react';
import { Modal, Form } from 'antd';
const formItemLayout = {
	labelCol: { span: 6 },
	wrapperCol: { span: 18 }
};

class ModalS extends React.Component {
	render() {
		const { modal } = this.props;
		const { getFieldDecorator } = this.props.form;
		return (
			<Modal {...modal} onOk={this.onSubmit}>
				{modal.isForm ? (
					<React.Fragment>
						{modal.formColumns &&
							modal.formColumns.map((item, i) => {
								return (
									!item.displayNone && (
										<Form.Item
											{...(modal.formItemLayout || formItemLayout)}
											label={item.label}
											key={item.key}
											{...item}
										>
											{item.getFieldDecorator
												? getFieldDecorator(`${item.key}`, {
														...item.getFieldDecorator
												  })(item.value)
												: item.value}
										</Form.Item>
									)
								);
							})}
						{/* 扩展formitem - 用于页面需求罗列的选项框 */
						modal.extendFormColumns &&
							modal.extendFormColumns.length > 0 &&
							modal.extendFormColumns.map(item => {
								return (
									<Form.Item
										{...(modal.formItemLayout || formItemLayout)}
										label={`${item.label}${item.id}`}
										key={item.id}
									>
										{getFieldDecorator(`${item.key}-${item.id}`, {
											rules: [
												{
													required: true,
													message: item.rulesMsg || '必填'
												}
											],
											...item.getFieldDecorator
										})(item.value)}
									</Form.Item>
								);
							})}
						{modal.otherContent && modal.otherContent}
					</React.Fragment>
				) : (
					modal.children
				)}

			</Modal>
		);
	}
	onSubmit = () => {
		if (this.props.modal.isForm) {
			this.props.form.validateFieldsAndScroll((err, values) => {
				if (!err) {
					this.props.modal.onOk(values);
				}
			});
		} else {
			this.props.modal.onOk();
		}
	};
}
const WrapperForm = Form.create({})(ModalS);
export default WrapperForm;
