import React from 'react';
const { moment } = window.GLOBAL_PARAMS;

/* 设备台账信息 */
export function setDeviceStandingColumns({ cols,handleDailyDetail }) {
  let columns = []
  cols.map(col=>{
    let item = {title:col.titleValue,dataIndex:col.titleKey,key:col.titleKey,render:value=>value || '-'}
    if(item.key === 'deviceTime'){
      item.render = (value) => value ? moment(value).format('YYYY-MM-DD') : '-';
    }
    columns.push(item)
  })
  columns = columns.concat([
    {
      title: '操作', dataIndex: 'id', key: 'id',
      render: (value, record) => <span style={{ color: '#6236FF', cursor: 'pointer' }} onClick={() => handleDailyDetail(record)}>详情</span>
    },
  ])
  return columns;
}
