import React, { useState, useRef, useCallback, useMemo } from 'react';
import { Layout, Button, Menu, Dropdown, Divider } from 'antd';
import ReactFlow, {
  ReactFlowProvider,
  addEdge,
  useNodesState,
  useEdgesState,
  Controls,
  MiniMap
} from 'react-flow-renderer';

import Sidebar from './Sidebar';
import Node1 from './Node/Node1'

import './index.css';
const { Header, Sider, Content } = Layout;
const initialNodes = [
  {
    id: '1',
    type: 'input',
    data: { label: 'input node' },
    position: { x: 250, y: 5 },
  },
];
const nodeTypes = { node1: Node1 };

let id = 0;
const getId = () => `dndnode_${id++}`;

const DnDFlow = () => {
  const reactFlowWrapper = useRef(null);
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  const [reactFlowInstance, setReactFlowInstance] = useState(null);

  const onConnect = useCallback((params) => setEdges((eds) => addEdge(params, eds)), []);

  const onDragOver = useCallback((event) => {
    event.preventDefault();
    event.dataTransfer.dropEffect = 'move';
  }, []);

  const onDrop = useCallback(
    (event) => {
      event.preventDefault();

      const reactFlowBounds = reactFlowWrapper.current.getBoundingClientRect();
      const type = event.dataTransfer.getData('application/reactflow');

      // check if the dropped element is valid
      if (typeof type === 'undefined' || !type) {
        return;
      }

      const position = reactFlowInstance.project({
        x: event.clientX - reactFlowBounds.left,
        y: event.clientY - reactFlowBounds.top,
      });
      const newNode = {
        id: getId(),
        type,
        position,
        data: { label: `${type} node` },
      };

      setNodes((nds) => nds.concat(newNode));
    },
    [reactFlowInstance]
  );

  const onSave = useCallback(() => {
    if (reactFlowInstance) {
      const flow = reactFlowInstance.toObject();
      console.log(flow);
      localStorage.setItem('example-flow', JSON.stringify(flow));
    }
  }, [reactFlowInstance]);


  return (
    <Layout>
      <Header className='header'>
        <Button type="primary" className='save'>保存</Button>
      </Header>
      <Layout>
        <Content>
          <Layout>
            <Header>

            </Header>
            <Content>
              <div className="dndflow">
                <ReactFlowProvider>
                  <Content>
                    <div className="reactflow-wrapper" ref={reactFlowWrapper}>
                      <ReactFlow
                        nodes={nodes}
                        edges={edges}
                        onNodesChange={onNodesChange}
                        onEdgesChange={onEdgesChange}
                        onConnect={onConnect}
                        onInit={setReactFlowInstance}
                        onDrop={onDrop}
                        onDragOver={onDragOver}
                        nodeTypes={nodeTypes}
                        fitView
                      >
                        <Controls />
                      </ReactFlow>
                    </div>
                  </Content>
                  <Sidebar />
                  <MiniMap />
                  <button onClick={onSave}>save</button>
                </ReactFlowProvider>
              </div>
            </Content>
          </Layout>
        </Content>
        <Sider>
          111
        </Sider>
      </Layout>

    </Layout>

  );
};

export default DnDFlow;
