/*
 * @Author: EmberCCC 1810888456@qq.com
 * @Date: 2022-07-06 22:04:09
 * @LastEditors: EmberCCC 1810888456@qq.com
 * @LastEditTime: 2022-07-06 22:54:14
 * @FilePath: \bl-device-manage-test\src\layouts\XTableLayout\index.js
 * @Description: 这是默认设置,请设置`customMade`, 打开koroFileHeader查看配置 进行设置: https://github.com/OBKoro1/koro1FileHeader/wiki/%E9%85%8D%E7%BD%AE
 */
import { toJS } from 'mobx'
import { inject, observer } from 'mobx-react'
import React, { useEffect } from 'react'
import { Search, Table, useTable, withTable } from 'table-render';
import stores from 'stores'



const XTableLayout = observer(({ TableStore, HomeStore }) => {
    const { columns } = TableStore
    useEffect(() => {
        console.log(toJS(columns));
        console.log(toJS(dataSource));
    }, [])
    const dataSource = [
        {
            "1": "沉降车间",
            "45": "测试编辑",
            "49": "ullamco",
            "createPerson": "Misaki",
            "createTime": "2022-06-28 20:59:54",
            "updateTime": "2022-07-06 21:31:42",
            "key": 3
        },
        {
            "1": "管道化车间",
            "45": "测试编辑",
            "49": "ullamco",
            "createPerson": "Misaki",
            "createTime": "2022-06-28 20:59:54",
            "updateTime": "2022-07-06 21:31:42",
            "key": 4
        },
        {
            "1": "生产车间",
            "49": "ullamco",
            "createPerson": "Misaki",
            "createTime": "2022-06-28 20:59:54",
            "updateTime": "2022-07-06 21:31:42",
            "key": 5
        },
        {
            "1": "铸造车间",
            "49": "ullamco",
            "createPerson": "Misaki",
            "createTime": "2022-06-28 20:59:54",
            "updateTime": "2022-07-06 21:31:42",
            "key": 6
        },
        {
            "1": "车间1",
            "45": "人员1",
            "createPerson": "Misaki",
            "createTime": "2022-07-06 14:39:22",
            "updateTime": "2022-07-06 14:39:22",
            "key": 87
        },
        {
            "1": "车间二",
            "45": "人员二",
            "createPerson": "Misaki",
            "createTime": "2022-07-06 14:59:23",
            "updateTime": "2022-07-06 14:59:23",
            "key": 88
        },
        {
            "1": "底盘车间",
            "45": "人员三",
            "createPerson": "Misaki",
            "createTime": "2022-07-06 18:41:53",
            "updateTime": "2022-07-06 18:41:53",
            "key": 89
        },
        {
            "1": "底盘车间",
            "45": "人员四",
            "createPerson": "Misaki",
            "createTime": "2022-07-06 18:42:19",
            "updateTime": "2022-07-06 18:42:19",
            "key": 90
        }
    ]
    const searchApi = () => {
        return {
            rows:dataSource,
            total:dataSource.length
        }
    }
    const schema = {
        type: "object",
        labelWidth: 30,
        properties: {
            1: {
                check: {},
                title: "车间",
                type: "string",
                fieldId: 1
            },
            45: {
                check: {},
                title: "人员",
                type: "string",
                fieldId: 45
            },
            46: {
                check: {},
                title: "测试多条字段",
                type: "string"
            },
            47: {
                check: {},
                title: "测试多条字段",
                type: "string"
            },
            48: {
                check: {},
                title: "测试多条字段",
                type: "string"
            },
            49: {
                check: {},
                title: "测试多条字段",
                type: "string"
            }
        }
    }
    return (
        <>
            <Search api={searchApi} schema={schema}/>
            <Table
                pagination={{ pageSize: 3 }}
                columns={columns}
            />
        </>
    )
})

export default inject((stores) => ({ TableStore: stores.TableStore, HomeStore: stores.HomeStore }))(XTableLayout);
