import React, { Component } from 'react';
import { inject, observer } from 'mobx-react';
import { Button, Row, Input, Tabs } from 'antd';
import DepartmentTable from './DepartmentTable';
import DepartmentDialog from './DepartmentDialog';

@inject('SystemStore')
@observer
class DepartmentManager extends Component {
    render() {
        const { modalVisible, modalChange, columnsType } = this.props.SystemStore
        const { TabPane } = Tabs;
        return <div>
            <Tabs  defaultActiveKey="1" onChange={this.tabChange} >
                    <TabPane tab={<div><i className="iconfont icon-compy icon-spacing"></i>公司</div>} key="1" />
                    <TabPane tab={<div><i className="iconfont icon-cop icon-spacing"></i>合作伙伴</div>} key="2" />
                    {/*<TabPane tab="供应商" key="3" />*/}
            </Tabs>
            <div className="tab-content-bg">
                <Row style={{marginBottom:20}}>
                    {columnsType === '2' && <span>
                        <Input placeholder='请输入客户名称' allowClear style={{ width: 250 }} onChange={this.searchValue}></Input>
                        <Button type='primary' style={{ marginLeft: 10 }} onClick={this.search} className="btn-border-purple">查询</Button>
                    </span>}
                    <Button style={{ marginLeft: 10}} onClick={e => modalChange(0)} type='primary' className='add-btn'>
                        <i className="iconfont icon-add icon-spacing" style={{fontSize:'12px'}}></i>
                        新增
                    </Button>
                </Row>
                <DepartmentTable />
                {modalVisible && <DepartmentDialog />}
            </div>

        </div>;
    }
    searchValue = (e) => {
        this.props.SystemStore.customerName = e.target.value
    }
    tabChange = (e) => {
        this.props.SystemStore.tableData = []
        this.props.SystemStore.PageInfo = { pageIndex: 1, pageSize: 10 }
        const { getDeptData } = this.props.SystemStore
        this.props.SystemStore.columnsType = e
        getDeptData(e)
    }
    search = () => {
        const { getDeptData, columnsType, customerName } = this.props.SystemStore
        getDeptData(columnsType, { customerName })
    }
    componentWillUnmount() {
        this.props.SystemStore.clearData()
    }
}
export default DepartmentManager;
