/*
 * @Author: EmberCCC 1810888456@qq.com
 * @Date: 2022-07-04 12:43:55
 * @LastEditors: EmberCCC 1810888456@qq.com
 * @LastEditTime: 2022-07-07 21:42:45
 * @FilePath: \bl-device-manage-test\src\layouts\FormEdit\self_item\link_item.js
 * @Description: 这是默认设置,请设置`customMade`, 打开koroFileHeader查看配置 进行设置: https://github.com/OBKoro1/koro1FileHeader/wiki/%E9%85%8D%E7%BD%AE
 */
import React, { useState } from "react";
import { Button, Input, Modal } from "antd";
import Math_Modal from "../FormModal/Math_Modal";
import './index.css'
export const Submit_check = ({ value, onChange, ...rest }) => {
    const [math, setMath] = useState(false);
    const [relValue,setRelValue] = useState("");
    const [checkList,setCheckList] = useState([]);
    const { addons } = rest;
    const handleInput = (event) => {
        onChange(event.target.value);
    }
    const handleLink = () => {
        console.log(1);
    }
    const openMath = () => {
        setMath(true);
    }
    const closeMath = () => {
        setMath(false);
    }
    const handleClose = () => {
        closeMath();
        let list = checkList;
        list.push(relValue);
        setCheckList(list)
        onChange(checkList)
    }
    const handleClick = () => {
         console.log();
    }
    const title_item = (
        <div>
            <span className="title_left">提交校验</span>
            <span className="title_right">使用数学运算符编辑公式</span>
        </div>
    )
    return (
        <div style={{ display: 'flex', flexDirection: 'column', width: '100%' }}>
            <Button onClick={openMath}>添加校验条件</Button>
            {
                checkList.map((item,index) => {
                    return (
                        <div key={index} onClick={(item) => handleClick(item)} className='check_item'>{item['content']}</div>
                    )
                })
            }
            <Modal
                visible={math}
                onCancel={handleClose}
                title={title_item}
                footer={null}
                width={800}
                destroyOnClose={true}
            >
                <Math_Modal title={"公式"} setValue={setRelValue}/>
            </Modal>
        </div>
    )
}
