/*
 * @Author: EmberCCC 1810888456@qq.com
 * @Date: 2022-07-19 23:03:37
 * @LastEditors: EmberCCC 1810888456@qq.com
 * @LastEditTime: 2022-07-20 01:04:14
 * @FilePath: \bl-device-manage-test\src\layouts\SocketManage\inConLayout\index.js
 * @Description: 这是默认设置,请设置`customMade`, 打开koroFileHeader查看配置 进行设置: https://github.com/OBKoro1/koro1FileHeader/wiki/%E9%85%8D%E7%BD%AE
 */
import { Button, Input, Menu, Radio, Switch } from "antd";
import { inject, observer } from "mobx-react";
import React, { useEffect } from "react";

import './index.css'
const InConLayout = observer(({ SocketStore }) => {
    const { SelectKey } = SocketStore;
    useEffect(() => {
        SocketStore.getAllUsers();
    }, [])
    function getItem(label, key, icon, children, type) {
        return {
            key,
            icon,
            children,
            label,
            type,
        };
    }
    const items = [
        getItem('成员', 'g1', null, [getItem('全部成员', '全部成员'), getItem('离职成员', '离职成员')], 'group'),
        getItem('部门', 'g2', null, [getItem('Option 3', '3'), getItem('Option 4', '4')], 'group'),
    ]

    const handleChange = (value) => {
        console.log(value);
    }
    const handleClick = (value) => {
        console.log(value);
        SocketStore.setValue('SelectKey', value.key)
    }
    const handleSwitch = (value) => {
        console.log(value);
    }
    return (
        <div className="all">
            <div className="in_all">
                <div className="in_left">
                    <div className="inL_first">
                        <Radio.Group onChange={handleChange} defaultValue={'department'}>
                            <Radio.Button value='department'>部门</Radio.Button>
                            <Radio.Button value='role'>角色</Radio.Button>
                        </Radio.Group>
                    </div>
                    <Menu items={items} onClick={handleClick} mode='inline' theme='light' defaultSelectedKeys={[SelectKey]} />
                </div>
                <div className="in_right">
                    <div className="inR_first">
                        <div className="inRF_L">
                            {SelectKey}
                        </div>
                        <div className="inRF_R">
                            <Switch onChange={handleSwitch} size='small' />
                            <div className="switch_des">允许成员修改姓名</div>
                        </div>
                    </div>
                    <div className="inR_Tool">
                        <div>
                            <Button type="primary" style={{ marginRight: '10px' }}>邀请成员</Button>
                            <Button>导出</Button>
                        </div>
                        <Input style={{ width: '200px', marginRight:'20px' }} />
                    </div>
                </div>
            </div>
        </div>
    )
})

export default inject((stores) => ({ SocketStore: stores.SocketStore }))(InConLayout)
